    &lt;synthetic&gt; &lt;paramaccessor&gt; private[this] val m$1: Int = _;
    def &lt;init&gt;($outer: Close, m$1: Int): &lt;$anon: Function1&gt; = {
      anonfun$mixed$1.this.m$1 = m$1;
